import React, { useState, useEffect } from "react"
import PropTypes from "prop-types"
import { Nav, NavItem, NavLink } from 'reactstrap'
import { ListGroup, ListGroupItem, ListGroupItemHeading, ListGroupItemText, Container } from 'reactstrap'
import { BrowserRouter as Router, Route, Link, Switch } from 'react-router-dom'
import NewApartment from './pages/NewApartment'
import ApartmentIndex from './pages/ApartmentIndex'
import Home from './pages/Home'
import Header from './components/Header'

const App = props => {
  const [apartments, setApartments] = useState([])

  useEffect(() =>{
    getApartments()},[])

  const getApartments = () => {
    fetch("/apartments")
    .then(response => {
      return response.json()
    })
    .then(apartments => {
      setApartments(apartments)
    })
  }

  const deleteApartment = (apt, id) => {
    fetch(`/apartments/${id}`,
    {
      method: 'PUT',
      body: JSON.stringify({apartment: apt}),
      headers: {
        'Content-Type': 'application/json'
      }
    }).then(response => {
        setSuccess(true)
        getApartments()
      })
  }

  return (
    <Router>
      <Header
        logged_in={props.logged_in}
        sign_in_route={props.sign_in_route}
        sign_out_route={props.sign_out_route}
      />
      {props.logged_in &&
        <div>
          <Link to="/apartments/new">Start New Apartment Listing</Link><br/>
          <Link to="/apartments">See Your Apartment Listings</Link>
        </div>
      }
      <Switch>
        <Route exact path="/listings" render={() => <ApartmentIndex apts={apartments}/>} />
        <Route exact path="/listings/new" render={() => <NewApartment />} />
        {
          apartments.map((apt,index) => {
            return(
              <Route exact path={`/listings/${apt.id}`} render={() => <ApartmentProfile getApartments={getApartments} apt={apt}/>} />
            )
          })
        }
      </Switch>
    </Router>
  )
}

export default App
