import React, { useState } from 'react'
import { ListGroup, ListGroupItem, ListGroupItemHeading, ListGroupItemText, Button, Container } from 'reactstrap'

const ApartmentProfile = props => {
  const [success, setSuccess] = useState(false)

  const handleDelete = () => {
    fetch(`apartments/${props.apt.id}`, {
      headers: {
        "Content-Type": "application/json"
      },
      method: "DELETE"
    }).then(response => {
        if (response.ok) setSuccess(true)
        else setError(true)
    }).catch(error => {
        console.log("error:",error)
    })
  }

  return(
    <>
      <h3>Apartment Profile</h3>
      <a href="/">Home</a>
      <a href="/listings">Back to all your listings</a>
      <Container>
        <ListGroup>
          <ListGroupItem>
            {/*<img style={{width:"200px"}} src={ props.apt.image_url } />*/}
            <ListGroupItemHeading>{ props.apt.subject }</ListGroupItemHeading>
            <ListGroupItemText>
            { props.apt.city}, { props.apt.state } <br/>
            { props.apt.description }
            </ListGroupItemText>
          </ListGroupItem>
          <a href={`/listings/${props.apt.id}/edit`}> <Button>Edit Listing</Button></a>
          <Button onClick={ handleDelete }>Delete Listing</Button>
        </ListGroup>
      </Container>
    </>
  )
}

export default ApartmentProfile
