import React, { useState, useEffect } from "react"
import PropTypes from "prop-types"
import { Nav, NavItem, NavLink } from 'reactstrap'
import { ListGroup, ListGroupItem, ListGroupItemHeading, ListGroupItemText, Container } from 'reactstrap'
import { BrowserRouter as Router, Route, Link, Switch } from 'react-router-dom'
import NewApartment from './pages/NewApartment'
import ApartmentIndex from './pages/ApartmentIndex'
import Home from './pages/Home'

const App = props => {
  const [apartments, setApartments] = useState([])
  const [success, setSuccess] = useState(false)

  useEffect(() =>{
    getApartments()},[])

  const getApartments = () => {
    fetch("/apartments")
    .then(response => {
      return response.json()
    })
    .then(apartments => {
      setApartments(apartments)
    })
  }

  const createApartment = (info) =>{
    return fetch("/apartments", {
      method: 'POST',
      headers:{
        "Content-Type": "application/json"
      },
      body: JSON.stringify({apartment: info})
    })
    .then(response => {
      if(response.status === 200){
        getApartments()
      }
    })
  }

  const deleteApartment = (apt, id) => {
    fetch(`/apartments/${id}`,
    {
      method: 'PUT',
      body: JSON.stringify({apartment: apt}),
      headers: {
        'Content-Type': 'application/json'
      }
    }).then(response => {
        setSuccess(true)
        getApartments()
      })
  }

  return (
    <Router>
      <h1>Mi Casa</h1>
      {props.logged_in &&
        <div>
          <Link to="/apartments/new">Start New Apartment Listing</Link><br/>
          <Link to="/apartments">See Your Apartment Listings</Link>
        </div>
      }
      <Switch>
        <Route exact path="/listings" render={() => <ApartmentIndex apts={apartments}/>} />

      </Switch>
    </Router>
  )
}

export default App
