import React, { useState, useEffect } from 'react'
import { ListGroup, ListGroupItem, ListGroupItemHeading, ListGroupItemText, Button, Container } from 'reactstrap'

const ApartmentIndex = props => {
  const [apts, setApts] = useState([])

  useEffect(() =>{
    getApartments()},[])

  const getApartments = () => {
    fetch("/apartments")
    .then(response => {
      if(response.ok) return response.json()
    })
    .then(apts => {
      setApts(apts)
    })
  }
  return(
    <>
      <Container>
      <h3 style={{textAlign:"center",margin:"10px"}}>Apartment Listings</h3>
      <ListGroup>
        {apts.map((apt,index) => {
          return(
            <ListGroupItem key={index}>
              <ListGroupItemHeading><a href={`/listings/${apt.id}`}>{ apt.subject }</a> | {apt.bedroom_count} BD, {apt.bathroom_count} BA</ListGroupItemHeading>
              <ListGroupItemText>
              <strong>{ apt.city}, { apt.state } <br/>
              { apt.rent }</strong> <br/><br/>
              { apt.description }
              </ListGroupItemText>
            </ListGroupItem>
          )
        })}
      </ListGroup>
      </Container>
    </>
  )
}

export default ApartmentIndex
