import React, { useState, useEffect } from 'react'
import { ListGroup, ListGroupItem, ListGroupItemHeading, ListGroupItemText, Button, Container } from 'reactstrap'
import { Redirect } from 'react-router-dom'

const ApartmentProfile = props => {
  const [success, setSuccess] = useState(false)
  const [error, setError] = useState(false)
  const [currentApt, setCurrentApt] = useState({})

  useEffect(() =>{
    getApartment()},[])

  const getApartment = () => {
    fetch(`/apartments/${props.match.params.id}`)
    .then(response => {
      if(response.ok) return response.json()
    })
    .then(apt => {
      setCurrentApt(apt)
    })
  }

  const handleDelete = e => {
    e.preventDefault()
    deleteCat()
  }

  const deleteCat = () => {
    fetch(`http://localhost:3000/apartments/${currentApt.id}`, {
      headers: {
        "Content-Type": "application/json"
      },
      method: "DELETE"
    }).then(response => {
        if (response.ok) setSuccess(true)
        else setError(true)
    }).catch(error => {
        console.log("error:",error)
    })
  }

  return(
    <>
      <h3>Apartment Profile</h3>
      <Container>
        <ListGroup>
          <ListGroupItem>
            {/*<img style={{width:"200px"}} src={ props.apt.image_url } />*/}
            <ListGroupItemHeading>{ currentApt.subject }</ListGroupItemHeading>
            <ListGroupItemText>
            { currentApt.city}, { currentApt.state } <br/>
            { currentApt.description }
            </ListGroupItemText>
          </ListGroupItem>
          <a href={`/listings/${currentApt.id}/edit`}> <Button>Edit Listing</Button></a>

          <a href="/listings">
            <Button type="submit" onClick={ handleDelete }>Delete Listing</Button>
            {success && <Redirect to="/listings" />}
          </a>

        </ListGroup>
      </Container>
    </>
  )
}

export default ApartmentProfile
