import React from "react"
import PropTypes from "prop-types"
import { Nav, NavItem, NavLink } from 'reactstrap'
import { ListGroup, ListGroupItem, ListGroupItemHeading, ListGroupItemText, Container } from 'reactstrap'
import { BrowserRouter as Router, Route, Link, Switch } from 'react-router-dom'
import NewApartment from './pages/NewApartment'
import Home from './pages/Home'

class App extends React.Component {
  render () {
    const {
      logged_in,
      sign_in_route,
      sign_out_route
    } = this.props

    return (
      <Router>
        <Nav>
          {logged_in &&
            <NavItem>
            <NavLink href={sign_out_route}>Sign Out</NavLink>
            </NavItem>
          }
          {!logged_in &&
            <NavItem>
            <NavLink href={sign_in_route}>Sign In</NavLink>
            </NavItem>
          }
        </Nav>
        <h1>Mi Casa</h1>
        {logged_in &&
          <div>
            <Link to="/apartments/new">Start New Apartment Listing</Link><br/>
            <Link to="/apartments">See Your Apartment Listings</Link>
          </div>
        }
        <Switch>
          <Route exact path="/" render={() => <Home apts={this.props.apartments}/>} />
          <Route exact path="/apartments" render={() => <ApartmentIndex />} />
          {
            this.props.apartments.map((apt,index) => {
              return(
                <Route exact path={`/apartments/${apt.id}`} render={() => <ApartmentProfile apt={apt} index={index}/> } />
              )
            })
          }
          {
            this.props.apartments.map((apt,index) => {
              return(
                <Route exact path={`/apartments/${apt.id}/edit`} render={() => <EditApartment apt={apt} index={index}/> } />
              )
            })
          }
        </Switch>
      </Router>
    )
  }
}

export default App
